import{_ as l}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as i,c as p,f as o}from"./app.d5818131.js";const e={},r=o("<hr><p>\u6570\u636E\u5E93\u4E09\u7EA7\u6A21\u5F0F\u7684<code>\u6A21\u5F0F\u3001\u5185\u6A21\u5F0F\u3001\u5916\u6A21\u5F0F\u5206\u522B\u5BF9\u5E94\u57FA\u672C\u8868\uFF08\u8868\u7EA7\uFF09\u3001\u5B58\u50A8\u6587\u4EF6\uFF08\u6587\u4EF6\u7EA7\uFF09\u3001\u89C6\u56FE\uFF08\u89C6\u56FE\u7EA7\uFF09</code>\u3002</p><p>\u805A\u7C07\u7D22\u5F15\u4F1A\u5C06\u5177\u4F53\u7684\u7269\u7406\u5B58\u50A8\u6539\u4E3A\u987A\u5E8F\u6A21\u5F0F\uFF0C\u6240\u4EE5\u5C5E\u4E8E\u5185\u6A21\u5F0F\u3002</p><hr><p>\u6570\u636E\u5E93\u5728\u4E09\u7EA7\u6A21\u5F0F\u4E4B\u95F4\u63D0\u4F9B\u4E86\u4E24\u7EA7\u6620\u50CF\uFF1A\u6A21\u5F0F/\u5185\u6A21\u5F0F\u6620\u50CF\u3001\u5916\u6A21\u5F0F/\u6A21\u5F0F\u6620\u50CF\u3002\u6B63\u56E0\u4E3A\u8FD9\u4E24\u96C6\u6620\u50CF\u4FDD\u8BC1\u4E86\u6570\u636E\u5E93\u4E2D\u7684\u6570\u636E\u5177\u6709\u8F83\u9AD8\u7684\u903B\u8F91\u72EC\u7ACB\u6027\u548C\u7269\u7406\u72EC\u7ACB\u6027\u3002</p><ul><li>\u6A21\u5F0F/\u5185\u6A21\u5F0F\u7684\u6620\u50CF\uFF1A\u5B58\u5728\u4E8E\u6982\u5FF5\u7EA7\u548C\u5185\u90E8\u7EA7\u4E4B\u95F4\uFF0C\u5B9E\u73B0\u4E86\u6982\u5FF5\u6A21\u5F0F\u5230\u5185\u6A21\u5F0F\u4E4B\u95F4\u7684\u8F6C\u6362\u3002</li><li>\u5916\u6A21\u5F0F/\u6A21\u5F0F\u7684\u6620\u50CF\uFF1A\u5B58\u5728\u4E8E\u5916\u90E8\u7EA7\u548C\u6982\u5FF5\u7EA7\u4E4B\u95F4\uFF0C\u5B9E\u73B0\u4E86\u5916\u6A21\u5F0F\u5230\u6982\u5FF5\u6A21\u5F0F\u4E4B\u95F4\u7684\u8F6C\u6362\u3002</li></ul><p>\u8BB0\u5FC6\uFF1A<code>\u6A21\u5F0F\u3001\u5185\u6A21\u5F0F\u3001\u5916\u6A21\u5F0F\u5206\u522B\u5BF9\u5E94\u6982\u5FF5\u6A21\u5F0F\u3001\u5185\u6A21\u5F0F\u3001\u5916\u6A21\u5F0F</code></p><hr><p>\u5408\u5E76ER\u56FE</p><ol><li>\u5C5E\u6027\u51B2\u7A81\uFF1A\u5C5E\u6027\u57DF\u51B2\u7A81\u548C\u5C5E\u6027\u53D6\u503C\u51B2\u7A81\uFF08\u6BCF\u4E2A\u5C5E\u6027\u7684\u53D6\u503C\u8303\u56F4\u5BF9\u5E94\u4E00\u4E2A\u503C\u7684\u96C6\u5408\uFF0C\u79F0\u4E3A\u8BE5\u5C5E\u6027\u7684\u57DF\uFF09</li><li>\u547D\u540D\u51B2\u7A81\uFF1A\u540C\u540D\u5F02\u4E49\u548C\u5F02\u540D\u540C\u4E49</li><li>\u63A5\u53E3\u51B2\u7A81\uFF1A\u540C\u4E00\u5BF9\u8C61\u5728\u4E0D\u540C\u5E94\u7528\u4E2D\u5177\u6709\u4E0D\u540C\u7684\u62BD\u8C61\uFF0C\u540C\u4E00\u5B9E\u4F53\u5728\u4E0D\u540C\u5C40\u90E8ER\u56FE\u4E2D\u6240\u5305\u542B\u7684\u5C5E\u6027\u683C\u5F0F\u548C\u5C5E\u6027\u6392\u5E8F\u4E0D\u5B8C\u5168\u76F8\u540C</li></ol><hr><p>\u5173\u7CFB\u4EE3\u6570\u4E0E\u5143\u7EC4\u6F14\u7B97</p><p>\u5143\u7EC4\uFF08tuple\uFF09\u662F\u5173\u7CFB\u6570\u636E\u5E93\u4E2D\u7684\u57FA\u672C\u6982\u5FF5\uFF0C\u5173\u7CFB\u662F\u4E00\u5F20\u8868\uFF0C\u8868\u4E2D\u7684\u6BCF\u884C\uFF08\u5373\u6570\u636E\u5E93\u4E2D\u7684\u6BCF\u6761\u8BB0\u5F55\uFF09\u5C31\u662F\u4E00\u4E2A\u5143\u7EC4\uFF0C\u6BCF\u5217\u5C31\u662F\u4E00\u4E2A\u5C5E\u6027\u3002</p><p>\u8FD0\u7B97\uFF1A</p><ul><li>\u5E76(\u222A)\uFF1A\u7ED3\u679C\u4E3A\u4E24\u8005\u5143\u7EC4\u4E4B\u548C\u53BB\u9664\u91CD\u590D\u884C</li><li>\u4EA4(\u2229)\uFF1A\u7ED3\u679C\u4E3A\u4E24\u8005\u5143\u7EC4\u7684\u91CD\u590D\u884C</li><li>\u5DEE(-)\uFF1A\u7ED3\u679C\u4E3A\u524D\u8005\u53BB\u9664\u91CD\u590D\u884C</li><li>\u7B1B\u5361\u5C14\u79EF(x)\uFF1A\u7ED3\u679C\u5217\u6570\u4E3A\u4E24\u8005\u5C5E\u6027\u5217\u4E4B\u548C\uFF0C\u884C\u6570\u4E3A\u4E24\u8005\u5143\u7D20\u6570\u7684\u4E58\u79EF</li><li>\u6295\u5F71(\u03C0\uFF1Aselect)\uFF1A\u5BF9\u5C5E\u6027\u5217\u7684\u9009\u62E9\u5217\u51FA</li><li>\u9009\u62E9(\u03C3\uFF1Awhere)\uFF1A\u5BF9\u5143\u7EC4\u884C\u7684\u9009\u62E9\u5217\u51FA</li><li>\u81EA\u7136\u8FDE\u63A5(\u22C8)\uFF1A\u7ED3\u679C\u5217\u4E3A\u4E24\u8005\u5C5E\u6027\u5217\u6570\u4E4B\u548C\u51CF\u53BB\u91CD\u590D\u884C\uFF0C\u884C\u6570\u4E3A\u4E24\u8005\u540C\u540D\u5C5E\u6027\u5217\u5176\u503C\u76F8\u540C\u7684\u7ED3\u679C\u5143\u7EC4</li></ul><p>\u5173\u7CFB\u4EE3\u6570\u8868\u8FBE\u5F0F\u67E5\u8BE2\u4F18\u5316\u7684\u539F\u5219\u5982\u4E0B\uFF1A</p><ol><li>\u5C3D\u65E9\u6267\u884C\u9009\u53D6\u8FD0\u7B97\u3002</li><li>\u5408\u5E76\u4E58\u79EF\u4E0E\u5176\u540E\u7684\u9009\u62E9\u8FD0\u7B97\u4E3A\u8FDE\u63A5\u8FD0\u7B97\u3002</li><li>\u5C06\u6295\u5F71\u8FD0\u7B97\u4E0E\u5176\u540E\u7684\u5176\u4ED6\u8FD0\u7B97\u540C\u65F6\u8FDB\u884C\uFF0C\u4EE5\u907F\u514D\u91CD\u590D\u626B\u63CF\u5173\u7CFB\u3002</li><li>\u5C06\u6295\u5F71\u4E0E\u5176\u524D\u540E\u7684\u4E8C\u76EE\u8FD0\u7B97\u7ED3\u5408\u8D77\u6765\uFF0C\u662F\u7684\u6CA1\u6709\u5FC5\u8981\u4E3A\u53BB\u6389\u67D0\u4E9B\u5B57\u6BB5\u518D\u626B\u63CF\u4E00\u904D\u5173\u7CFB\u3002</li><li>\u5728\u6267\u884C\u8FDE\u63A5\u524D\u5BF9\u5173\u7CFB\u9002\u5F53\u5730\u9884\u5904\u7406\uFF0C\u5C31\u80FD\u5FEB\u901F\u5730\u627E\u5230\u8981\u8FDE\u63A5\u7684\u5143\u7EC4\u3002\uFF08\u65B9\u6CD5\u6709\u4E24\u79CD\uFF1A\u7D22\u5F15\u8FDE\u63A5\u6CD5\u3001\u6392\u5E8F\u5408\u5E76\u8FDE\u63A5\u6CD5\uFF09</li><li>\u5B58\u50A8\u516C\u5171\u5B50\u8868\u8FBE\u5F0F\u3002</li></ol><hr><p>\u89C4\u8303\u5316\u7406\u8BBA\u7684\u610F\u4E49\uFF1A\u89E3\u51B3\u975E\u89C4\u8303\u5316\u7684\u5173\u7CFB\u6A21\u5F0F\u4E2D\u53EF\u80FD\u5B58\u5728\u7684\u95EE\u9898\uFF1A\u6570\u636E\u5197\u4F59\u3001\u66F4\u65B0\u5F02\u5E38\u3001\u63D2\u5165\u5F02\u5E38\u3001\u5220\u9664\u5F02\u5E38</p><hr><p>\u4E8B\u52A1\u7684ACID\u7279\u5F81</p><ul><li>\u539F\u5B50\u6027\uFF08Atomicity\uFF09\uFF1A\u4E8B\u52A1\u662F\u539F\u5B50\u7684\uFF0C\u8981\u4E48\u90FD\u505A\u8981\u4E48\u90FD\u4E0D\u505A\u3002</li><li>\u4E00\u81F4\u6027\uFF08Consistency\uFF09\uFF1A\u5728\u4E8B\u52A1\u5F00\u59CB\u4E4B\u524D\u548C\u4E8B\u52A1\u7ED3\u675F\u4EE5\u540E\uFF0C\u6570\u636E\u5E93\u7684\u5B8C\u6574\u6027\u6CA1\u6709\u88AB\u7834\u574F\u3002</li><li>\u9694\u79BB\u6027\uFF08Isolation\uFF09\uFF1A\u4E8B\u52A1\u76F8\u4E92\u9694\u79BB\u3002</li><li>\u6301\u4E45\u6027\uFF08Durability\uFF09\uFF1A\u4E8B\u52A1\u5904\u7406\u7ED3\u675F\u540E\uFF0C\u5BF9\u6570\u636E\u7684\u4FEE\u6539\u5C31\u662F\u6C38\u4E45\u7684\u3002</li></ul><hr><p>\u6570\u636E\u5E93\u8BBE\u8BA1\u8FC7\u7A0B\uFF1A</p><ol><li>\u9700\u6C42\u5206\u6790\uFF1A\u4EA7\u51FA\u6570\u636E\u6D41\u56FE\u3001\u6570\u636E\u5B57\u5178\u3001\u9700\u6C42\u8BF4\u660E\u4E66</li><li>\u6982\u5FF5\u7ED3\u6784\u8BBE\u8BA1(\u6982\u5FF5\u6A21\u578B\uFF0CE-R\u56FE)\uFF1A\u5C06\u7528\u6237\u9700\u6C42\u62BD\u8C61\u4E3A\u4FE1\u606F\u7ED3\u6784\uFF0C\u4EA7\u51FAE-R\u56FE</li><li>\u903B\u8F91\u7ED3\u6784\u8BBE\u8BA1(\u903B\u8F91\u6A21\u5F0F\u3001\u5916\u6A21\u5F0F)\uFF1A\u628A\u57FA\u672CE-R\u56FE\u8F6C\u6362\u4E3A\u4E0E\u9009\u7528\u6570\u636E\u5E93\u7BA1\u7406\u7CFB\u7EDF\u4EA7\u54C1\u6240\u652F\u6301\u7684\u6570\u636E\u6A21\u578B\u76F8\u7B26\u5408\u7684\u903B\u8F91\u7ED3\u6784\uFF0C\u4EA7\u51FA\u5173\u7CFB\u6A21\u5F0F</li><li>\u7269\u7406\u8BBE\u8BA1(\u5185\u6A21\u5F0F):\u4E3A\u4E00\u4E2A\u7ED9\u5B9A\u7684\u903B\u8F91\u6570\u636E\u6A21\u578B\u9009\u53D6\u4E00\u4E2A\u6700\u9002\u5408\u5E94\u7528\u8981\u6C42\u7684\u7269\u7406\u7ED3\u6784\u7684\u8FC7\u7A0B\uFF0C\u5C31\u662F\u6570\u636E\u5E93\u7684\u7269\u7406\u8BBE\u8BA1</li></ol><hr><p>DBMS\uFF08\u6570\u636E\u5E93\u7BA1\u7406\u7CFB\u7EDF\uFF09\u628A\u4E8B\u52A1\u5F00\u59CB\u3001\u4E8B\u52A1\u7ED3\u675F\u4EE5\u53CA\u5BF9\u6570\u636E\u5E93\u7684\u63D2\u5165\u3001\u5220\u9664\u3001\u4FEE\u6539\u7684\u6BCF\u4E00\u6B21\u64CD\u4F5C\u5199\u5165\u65E5\u5FD7\u6587\u4EF6\uFF0C\u6309\u4E00\u5B9A\u65F6\u95F4\u95F4\u9694\uFF0C\u628A\u6570\u636E\u7F13\u5B58\u533A\u7684\u5185\u5BB9\u5199\u5165\u6570\u636E\u6587\u4EF6\u3002</p><p>DBMS\u7684\u5B89\u5168\u63AA\u65BD\u67093\u4E2A\u65B9\u9762\uFF1A\u6743\u9650\u673A\u5236\uFF0C\u89C6\u56FE\u673A\u5236\uFF0C\u6570\u636E\u52A0\u5BC6</p><hr><p>\u6392\u5B83\u9501\uFF1AExclusive Locks\uFF0C\u7B80\u79F0X\u9501\uFF0C\u53C8\u79F0\u5199\u9501\u3002\u4E8B\u52A1A\u5BF9\u6570\u636E\u4E0AX\u9501\uFF0C\u5C31\u53EA\u6709A\u80FD\u8BFB\u5199\u6570\u636E\uFF0C\u5176\u4ED6\u4E8B\u52A1\u5747\u4E0D\u80FD\u52A0\u4EFB\u4F55\u9501\uFF0C\u4ECE\u800C\u4E0D\u80FD\u8BFB\u5199\u6570\u636E\u76F4\u5230A\u91CA\u653EX\u9501\u3002</p><p>\u5171\u4EAB\u9501\uFF1AShare Locks\uFF0C\u7B80\u79F0S\u9501\uFF0C\u53C8\u79F0\u8BFB\u9501\u3002\u4E8B\u52A1A\u5BF9\u6570\u636E\u4E0AS\u9501\uFF0CA\u5C31\u53EA\u80FD\u8BFB\u53D6\u6570\u636E\u800C\u4E0D\u80FD\u4FEE\u6539\uFF0C\u5176\u4ED6\u4E8B\u52A1\u4E5F\u80FD\u5BF9\u6570\u636E\u4E0AS\u9501\u6765\u8BFB\u53D6\uFF0C\u4F46\u662F\u53EA\u8981\u6570\u636E\u4E0A\u6709S\u9501\uFF0C\u4EFB\u4F55\u4E8B\u52A1\u90FD\u53EA\u80FD\u5BF9\u5176\u52A0S\u9501\u800C\u4E0D\u80FD\u52A0X\u9501\u3002</p><hr><p>\u8BBEU\u662F\u5173\u7CFB\u6A21\u5F0FR\u7684\u5C5E\u6027\u96C6\uFF0CF\u662FR\u4E0A\u6210\u7ACB\u7684\u81F4\u8BBE\u8BA1U\u4E2D\u5C5E\u6027\u7684\u884C\u6570\u4EE5\u4F9D\u8D56\u96C6\u3002\u884C\u6570\u4EE5\u6765\u7684\u63A8\u7406\u89C4\u5219\u6709\u4EE5\u4E0B\u4E09\u6761\uFF1A</p><ul><li>\u81EA\u53CD\u5F8B\uFF1A\u82E5\u5C5E\u6027\u96C6Y\u5305\u542B\u4E8E\u5C5E\u6027\u96C6X\uFF0C\u5C5E\u6027\u96C6X\u5305\u542B\u4E8EU\uFF0C\u5219X\u2192Y\u5728R\u4E0A\u6210\u7ACB\uFF08\u6B64\u5904X\u2192Y\u662F\u5E73\u51E1\u51FD\u6570\u4F9D\u8D56\uFF09\u3002</li><li>\u589E\u5E7F\u5F8B\uFF1A\u82E5X\u2192Y\u5728R\u4E0A\u6210\u7ACB\uFF0C\u4E14\u5C5E\u6027\u96C6Z\u5305\u542B\u4E8E\u5C5E\u6027\u96C6U\uFF0C\u5219XZ\u2192YZ\u5728R\u4E0A\u6210\u7ACB\u3002</li><li>\u4F20\u9012\u5F8B\uFF1A\u82E5X\u2192Y\u548CY\u2192Z\u5728R\u4E0A\u6210\u7ACB\uFF0C\u5219X\u2192Z\u4E0A\u6210\u7ACB\u3002</li></ul><p>\u6839\u636E\u5F02\u5E38\u4E09\u6761\u63A8\u7406\u89C4\u5219\uFF0C\u53EF\u5F97\u51FA\u4EE5\u4E0B\u4E09\u6761\u63A8\u7406\u89C4\u5219\uFF1A</p><ul><li>\u5408\u5E76\u89C4\u5219\uFF1A\u82E5X\u2192Y\uFF0CX\u2192Z\uFF0C\u5219X\u2192YZ\u4E3AF\u6240\u8574\u542B</li><li>\u4F2A\u4F20\u9012\u89C4\u5219\uFF1A\u82E5X\u2192Y\uFF0CWY\u2192Z\uFF0C\u5219XW\u2192Z\u4E3AF\u6240\u8574\u542B</li><li>\u5206\u89E3\u89C4\u5219\uFF1A\u82E5X\u2192Y\uFF0CZ\u2286Y\uFF0C\u5219X\u2192Z\u4E3AF\u6240\u8574\u542B</li></ul><hr><p>\u5206\u5E03\u5F0F\u6570\u636E\u5E93</p><ul><li>\u5206\u7247\u900F\u660E\u6027\uFF1A\u7528\u6237\u4E0D\u5FC5\u5173\u5FC3\u6570\u636E\u662F\u5982\u4F55\u5206\u7247\u7684\uFF0C\u4ED6\u4EEC\u5BF9\u6570\u636E\u7684\u64CD\u4F5C\u5728\u5168\u5C40\u5173\u7CFB\u4E0A\u8FDB\u884C\u7684\uFF0C\u5373\u5173\u5FC3\u5982\u4F55\u5206\u7247\u5BF9\u7528\u6237\u662F\u900F\u660E\u7684\uFF0C\u56E0\u6B64\uFF0C\u5F53\u5206\u7247\u6539\u53D8\u65F6\u5E94\u7528\u7A0B\u5E8F\u53EF\u4EE5\u4E0D\u53D8\u3002\u5206\u7247\u900F\u660E\u662F\u6700\u9AD8\u5C42\u6B21\u7684\u900F\u660E\u6027\u3002</li><li>\u590D\u5236\u900F\u660E\u6027\uFF1A\u7528\u6237\u4E0D\u5173\u5FC3\u6570\u636E\u5E93\u5728\u7F51\u7EDC\u4E2D\u7684\u5404\u4E2A\u8282\u70B9\u7684\u590D\u5236\u60C5\u51B5\uFF0C\u88AB\u590D\u5236\u7684\u6570\u636E\u66F4\u65B0\u90FD\u7531\u7CFB\u7EDF\u81EA\u52A8\u5B8C\u6210\u3002</li><li>\u4F4D\u7F6E\u900F\u660E\u6027\uFF1A\u7528\u6237\u4E0D\u5FC5\u77E5\u9053\u6240\u64CD\u4F5C\u7684\u6570\u636E\u653E\u5728\u4F55\u5904\uFF0C\u5373\u6570\u636E\u5206\u914D\u5230\u90A3\u4E2A\u6216\u90A3\u4E9B\u7AD9\u70B9\u5B58\u50A8\u5BF9\u7528\u6237\u662F\u900F\u660E\u7684\u3002\u56E0\u6B64\uFF0C\u6570\u636E\u5206\u7247\u6A21\u5F0F\u7684\u6539\u53D8\uFF0C\u5982\u628A\u6570\u636E\u4ECE\u4E00\u4E2A\u7AD9\u70B9\u8F6C\u79FB\u5230\u53E6\u4E00\u4E2A\u7AD9\u70B9\u5C06\u4E0D\u4F1A\u5F71\u54CD\u5E94\u7528\u7A0B\u5E8F\uFF0C\u56E0\u6B64\u5E94\u7528\u7A0B\u5E8F\u4E0D\u5FC5\u6539\u5199\u3002</li><li>\u903B\u8F91\u900F\u660E\u6027(\u5C40\u90E8\u6620\u50CF\u900F\u660E\u6027)\uFF1A\u6700\u4F4E\u5C42\u6B21\u7684\u900F\u660E\u6027\uFF0C\u8BE5\u900F\u660E\u6027\u63D0\u4F9B\u6570\u636E\u5230\u5C40\u90E8\u6570\u636E\u5E93\u7684\u6620\u50CF\uFF0C\u5373\u7528\u6237\u4E0D\u5FC5\u5173\u5FC3\u5C40\u90E8DBMS\u652F\u6301\u54EA\u79CD\u6570\u636E\u6A21\u578B\u3001\u4F7F\u7528\u54EA\u79CD\u6570\u636E\u64CD\u7EB5\u8BED\u8A00\uFF0C\u6570\u636E\u6A21\u578B\u548C\u64CD\u7EB5\u8BED\u8A00\u7684\u8F6C\u6362\u662F\u7531\u7CFB\u7EDF\u5B8C\u6210\u7684\u3002\u56E0\u6B64\uFF0C\u5C40\u90E8\u6620\u50CF\u900F\u660E\u6027\u5BF9\u5F02\u6784\u578B\u548C\u540C\u6784\u5F02\u8D28\u7684\u5206\u5E03\u5F0F\u6570\u636E\u5E93\u7CFB\u7EDF\u65F6\u975E\u5E38\u91CD\u8981\u3002</li></ul><hr><p>\u5305\u542B\u5728\u4EFB\u4F55\u4E00\u4E2A\u5019\u9009\u7801\u4E2D\u7684\u5C5E\u6027\u53EB\u4F5C\u4E3B\u5C5E\u6027\uFF0C\u5426\u5219\u53EB\u505A\u975E\u4E3B\u5C5E\u6027\u3002</p><hr><p>DDBS\uFF08\u5206\u5E03\u5F0F\u6570\u636E\u7CFB\u7EDF\uFF09\u7684\u57FA\u672C\u7279\u70B9\uFF1A</p><ol><li>\u7269\u7406\u5206\u5E03\u6027\uFF1A\u6570\u636E\u4E0D\u662F\u5B58\u50A8\u5728\u4E00\u4E2A\u573A\u5730\u4E0A\uFF0C\u4E8C\u662F\u5B58\u50A8\u5728\u8BA1\u7B97\u673A\u7F51\u7EDC\u7684\u591A\u4E2A\u573A\u5730\u4E0A</li><li>\u903B\u8F91\u6574\u4F53\u6027\uFF1A\u6570\u636E\u7269\u7406\u5206\u5E03\u5728\u5404\u4E2A\u573A\u5730\uFF0C\u4F46\u903B\u8F91\u4E0A\u662F\u4E00\u4E2A\u6574\u4F53\uFF0C\u5B83\u4EEC\u88AB\u6240\u6709\u7528\u6237\uFF08\u5168\u5C40\u7528\u6237\uFF09\u5171\u4EAB\uFF0C\u5E76\u7531\u4E00\u4E2ADDBMS\uFF08\u5206\u5E03\u5F0F\u6570\u636E\u5E93\u7BA1\u7406\u7CFB\u7EDF\uFF09\u7EDF\u4E00\u7BA1\u7406</li><li>\u573A\u5730\u81EA\u6CBB\u6027\uFF1A\u5404\u573A\u5730\u4E0A\u7684\u6570\u636E\u7531\u672C\u5730\u7684DBMS\uFF08\u6570\u636E\u5E93\u7BA1\u7406\u7CFB\u7EDF\uFF09\u7BA1\u7406\uFF0C\u5177\u6709\u81EA\u6CBB\u5904\u7406\u80FD\u529B\uFF0C\u5B8C\u6210\u672C\u573A\u5730\u7684\u5E94\u7528\uFF08\u5C40\u90E8\u5E94\u7528\uFF09</li><li>\u573A\u5730\u4E4B\u95F4\u534F\u4F5C\u6027\uFF1A\u5404\u573A\u5730\u867D\u7136\u5177\u6709\u9AD8\u5EA6\u7684\u81EA\u6CBB\u6027\uFF0C\u4F46\u662F\u53C8\u76F8\u4E92\u534F\u4F5C\u6784\u6210\u4E00\u4E2A\u6574\u4F53</li></ol><hr><p>\u54C8\u5E0C\u8868\u548C\u4E8C\u53C9\u6392\u5E8F\u6811\u90FD\u53EF\u4EE5\u5728\u67E5\u627E\u8FC7\u7A0B\u4E2D\u52A8\u6001\u521B\u5EFA\uFF0C\u5C5E\u4E8E\u52A8\u6001\u67E5\u627E\u8868\u3002</p><p>\u987A\u5E8F\u67E5\u627E\u65B9\u6CD5\u6309\u7167\u8BBE\u5B9A\u7684\u6B21\u5E8F\u4F9D\u6B21\u67E5\u627E\u8868\u4E2D\u5143\u7D20\u7684\u5173\u952E\u5B57\u8FDB\u884C\u6BD4\u8F83\uFF0C\u5728\u987A\u5E8F\u5B58\u50A8\u7ED3\u6784\u548C\u94FE\u8868\u7ED3\u6784\u4E0A\u90FD\u53EF\u4EE5\u5B9E\u73B0\u8BE5\u67E5\u627E\u8FC7\u7A0B\u3002</p><p>\u4E8C\u5206\u67E5\u627E\u9700\u8981\u5BF9\u4E2D\u95F4\u5143\u7D20\u8FDB\u884C\u5FEB\u901F\u5B9A\u4F4D\uFF0C\u5728\u94FE\u8868\u7ED3\u6784\u4E0A\u65E0\u6CD5\u5B9E\u73B0\u3002</p>",48),t=[r];function c(h,s){return i(),p("div",null,t)}const X=l(e,[["render",c],["__file","database-technology.html.vue"]]);export{X as default};
