import{_ as a}from"./plugin-vue_export-helper-c27b6911.js";import{r as n,o,c as s,d as t,e,b as d,f as h}from"./app-5668605f.js";const c={},i=h('<h2 id="考试简介" tabindex="-1"><a class="header-anchor" href="#考试简介" aria-hidden="true">#</a> 考试简介</h2><p>计算机技术与软件专业技术资格（水平）考试（以下简称计算机软件资格考试）是原中国计算机软件专业技术资格和水平考试（简称软件考试）的完善与发展。计算机软件资格考试是由国家人力资源和社会保障部、工业和信息化部领导下的国家级考试，其目的是科学、公正地对全国计算机与软件专业技术人员进行职业资格、专业技术资格认定和专业技术水平测试。工业和信息化部教育与考试中心负责全国考务管理工作，除台湾地区外，计算机软件资格考试在全国各省、自治区、直辖市及计划单列市和新疆生产建设兵团，以及香港特别行政区和澳门特别行政区，都建立了考试管理机构，负责本区域考试的组织实施工作。计算机软件资格考试设置了 27 个专业资格，涵盖 5 个专业领域， 3 个级别层次（初级、中级、高级）。计算机软件资格考试在全国范围内已经实施了二十多年，近十年来,考试规模持续增长，截止目前,累计报考人数约有五百万人。该考试由于其权威性和严肃性，得到了社会各界及用人单位的广泛认同，并为推动国家信息产业发展，特别是在软件和服务产业的发展,以及提高各类信息技术人才的素质和能力中发挥了重要作用。</p><p>根据原人事部、原信息产业部文件（国人部发[2003]39 号）文件规定，计算机软件资格考试纳入全国专业技术人员职业资格证书制度的统一规划，实行统一大纲、统一试题、统一标准、统一证书的考试办法，每年举行两次。通过考试获得证书的人员，表明其已具备从事相应专业岗位工作的水平和能力，用人单位可根据工作需要从获得证书的人员中择优聘任相应专业技术职务（技术员、助理工程师、工程师、高级工程师）。计算机软件资格考试全国统一实施后，不再进行计算机技术与软件相应专业和级别的专业技术职务任职资格评审工作。因此，计算机软件资格考试既是职业资格考试，又是职称资格考试。同时，该考试还具有水平考试性质，报考任何级别不需要学历、资历条件，只要达到相应的专业技术水平就可以报考相应的级别。计算机软件资格考试部分专业岗位的考试标准与日本、韩国相关考试标准实现了互认，中国信息技术人员在这些国家还可以享受相应的待遇。考试合格者将颁发由中华人民共和国人力资源和社会保障部、工业和信息化部用印的计算机技术与软件专业技术资格（水平）证书。该证书在全国范围内有效。</p><h2 id="资格设置" tabindex="-1"><a class="header-anchor" href="#资格设置" aria-hidden="true">#</a> 资格设置</h2><table><thead><tr><td></td><td>计算机软件</td><td>计算机网络</td><td>计算机应用技术</td><td>信息系统</td><td>信息服务</td></tr></thead><tbody><tr><td>高级资格</td><td colspan="5">信息系统项目管理师 系统分析师 系统架构设计师 网络规划设计师 系统规划与管理师</td></tr><tr><td>中级资格</td><td>软件测评师<br>软件设计师<br>软件过程能力评估师</td><td>网络工程师</td><td>多媒体应用设计师<br>嵌入式系统设计师<br>计算机辅助设计师<br>电子商务设计师</td><td>系统集成项目管理工程师<br>信息安全监理师<br>信息安全工程师<br>数据库系统工程师<br>信息系统管理工程师</td><td>计算机硬件工程师<br>信息技术支持工程师</td></tr><tr><td>初级资格</td><td>程序员</td><td>网络管理员</td><td>多媒体应用制作技术员<br>电子商务技术员</td><td>信息系统运行管理员</td><td>网页制作员<br>信息处理技术员</td></tr></tbody></table><h2 id="合格线" tabindex="-1"><a class="header-anchor" href="#合格线" aria-hidden="true">#</a> 合格线</h2><p>软考各科目<strong>全部</strong>达到合格线则本次考试视为通过</p>',7),l={href:"http://www.mohrss.gov.cn/SYrlzyhshbzb/fwyd/sykaoshi/zyjsryzyzgkshgbz/202202/t20220217_436122.html?bsh_bid=5674035974",target:"_blank",rel:"noopener noreferrer"},b=t("h2",{id:"官网",tabindex:"-1"},[t("a",{class:"header-anchor",href:"#官网","aria-hidden":"true"},"#"),e(" 官网")],-1),_={href:"https://www.ruankao.org.cn/",target:"_blank",rel:"noopener noreferrer"};function p(f,m){const r=n("ExternalLinkIcon");return o(),s("div",null,[i,t("p",null,[e("2022 年 2 月 16 日, 人力资源社会保障部办公厅发布了"),t("a",l,[e("关于 33 项专业技术人员职业资格考试实行相对固定合格标准有关事项的通告"),d(r)]),e(", 至此软考正式实施相对固定合格标准, 合格标准为试卷满分（75 分）的 60% 即 45 分")]),b,t("p",null,[t("a",_,[e("中国计算机技术资格网"),d(r)])])])}const k=a(c,[["render",p],["__file","index.html.vue"]]);export{k as default};
